insert into monumento(id, codigo_pais, nombre_pais, nombre_ciudad, latitud, longitud, nombre_monumento, descripcion, photo_url) values (1, 'CZ', 'Czech Republic', 'Bystřice nad Pernštejnem', 49.522946, 16.2614713, 'Kostel svatého Vavřince v Bystřici nad Pernštejnem', 'La Iglesia de San Lorenzo es un templo católico romano en la ciudad de Bystřice nad Pernštejnem, en el distrito de Žďár nad Sázavou. Es la iglesia parroquial de la parroquia católica romana de Bystřice nad Pernštejnem y un monumento cultural protegido de la República Checa.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6d/Byst%C5%99ice_nad_Pern%C5%A1tejnem_-_kostel.jpg/250px-Byst%C5%99ice_nad_Pern%C5%A1tejnem_-_kostel.jpg');
insert into monumento(id, codigo_pais, nombre_pais, nombre_ciudad, latitud, longitud, nombre_monumento, descripcion, photo_url) values (2, 'RU', 'Russia', 'Poykovskiy', 60.994897, 71.8982957, 'Monument to Gerasim Kurin', 'Gerasim Kurin es uno de los partisanos más famosos en la historia del Imperio Ruso. Fue el líder de un destacamento partisano campesino que operó durante la Guerra Patria de 1812 en el área del actual Pávlovsky Posad. Escultor A.A. Karasev.', 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/2c/81/12/9d/caption.jpg?w=1000&h=-1&s=1');
insert into monumento(id, codigo_pais, nombre_pais, nombre_ciudad, latitud, longitud, nombre_monumento, descripcion, photo_url) values (3, 'CN', 'China', 'Shenzhen', 29.04797, 105.944081, 'Shenzhen Window of the World', 'Window of the World es un parque de atracciones ubicado en la ciudad de Shenzhen en China. Tiene aproximadamente 130 reproducciones de algunas atracciones turísticas famosas de todo el mundo, ​ reunidas en una superficie de 48 hectáreas. La réplica de la Torre Eiffel domina el horizonte del lugar.', 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/0c/a0/bd/e8/window-of-the-world.jpg?w=1200&h=-1&s=1');
insert into monumento(id, codigo_pais, nombre_pais, nombre_ciudad, latitud, longitud, nombre_monumento, descripcion, photo_url) values (4, 'UA', 'Ukraine', 'Myhiya', 48.0398428, 30.9521026, 'Bugskiy Gard Natural Reserve', 'Hermosas vistas, maravillosa naturaleza, pesca y simplemente un buen descanso a orillas del Southern Bug. Excelentes lugares para el descanso, para familias, grupos o parejas. En general te divertirás mucho.', 'https://dynamic-media-cdn.tripadvisor.com/media/photo-o/12/4d/56/8d/caption.jpg?w=1200&h=-1&s=1');
